mapper.update:
update
PayBack set state=#{state}

	,realtime=#{datetime}


	,checktime=#{datetime}

where id=#{id}

mapper.select:
select * from PayBack
where state=#{state}

	and productId in
	
		#{item}
	

order by deadline asc

		select *
		from PayBack where borrowerAccountId=#{borrowerAccountId}
		
			and state in
			
				#{item}
			
		
		
            and deadline >= #{starttime}
		
		
            and deadline <= #{endtime}
		
		order by deadline asc
		LIMIT #{offset}, #{recnum}

		select count(*)
		from PayBack where
		borrowerAccountId=#{borrowerAccountId}
		
			and state in
			
				#{item}
			
		
		
            and deadline >= #{starttime}
		
		
            and deadline <= #{endtime}

		select count(*)
		from PayBack where
		borrowerAccountId=#{borrowerAccountId}
		
			and state >= #{state}
		
		
            and deadline >= #{starttime}
		
		
            and deadline <= #{endtime}

		select sum(a.chiefamount) chiefAmount,sum(a.interest) interest from  PayBack a,Product b where a.productId=b.id
		and a.borrowerAccountId=#{borrowerAccountId} and b.state=2
		
			and a.state in
			
				#{item}
			
		
        and a.deadline <= #{endtime}

select sum(a.chiefamount) chiefAmount,sum(a.interest) interest from  PayBack a,Product b where a.productId=b.id
and a.borrowerAccountId=#{borrowerAccountId}

    and a.state in
    
        #{item}
    

and a.realtime >= #{starttime}

mapper.update:
    	update
		PayBack set checkResult=#{checkResult} 
		where id=#{id}
